    private void loginBt_LoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginBt_LoginActionPerformed






        String pass = String.valueOf(passwordTxt_Login.getPassword());
        passnew = pass;

        try {
            Business bus = new Business();

            boolean ad = false;
            ad = ad_radio.isSelected();
            try {
                switch (bus.Login(usernameTxt_Login.getText(), pass, ad)) {
                    case 0:
                        this.progress(99);

                        loginStatus.setFont(new Font("Arial", 2, 13));
                        passwordTxt_Login.setText("");

                        break;
                    case 1:

                        adm = true;
                        this.toFront();
                        this.setEnabled(true);
                        welcome.setVisible(true);

                        this.progress(100);
                        loginFrame.setVisible(false);
                        binAdmin.setEnabled(false);
                        statusTxt.setText(usernameTxt_Login.getText() + " Type:Admin At: " + new Date());
                        Staff_Bt.setEnabled(ad);
                        subject_Bt.setEnabled(ad);
                        classBt.setEnabled(ad);
                        studentBt.setEnabled(ad);
                        courses_Bt.setEnabled(ad);
                        RecycleBin_Bt.setEnabled(ad);

                        pass_Bt.setEnabled(ad);
                        logout_Bt.setEnabled(ad);
                        if (usernameTxt_Login.getText().equals("admin")) {
                            admin_Bt.setEnabled(ad);
                            attendance_Bt.setEnabled(true);
                            Sum_Bt.setEnabled(true);
                            binAdmin.setEnabled(true);
                        }
                        break;
                    case 2:
                        pass_Bt.setEnabled(true);
                        logout_Bt.setEnabled(true);
                        attendance_Bt.setEnabled(true);
                        Sum_Bt.setEnabled(true);
                        welcome.setVisible(true);
                        loginFrame.setVisible(false);
                        this.toFront();
                        this.setEnabled(true);


                        this.progress(100);



                        statusTxt.setText(usernameTxt_Login.getText() + " Type:Staff At: " + new Date());

                        break;
                }
            } catch (SQLException ex) {
                Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
            }

        } catch (SQLException s) {
            config = true;
            JOptionPane.showMessageDialog(this, "Don't connect to database ! \n Please try connection config again!", "Erorr!", 2);
            configFrame.setSize(600, 800);
            MainFrame.add(configFrame);
            try {
                configFrame.setMaximum(true);
                configFrame.toFront();
            } catch (PropertyVetoException ex) {
                Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
            }
        }


    }//GEN-LAST:event_loginBt_LoginActionPerformed

