    private void enterBt_ClassEditorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_enterBt_ClassEditorActionPerformed
        progress(200);
        if (updateClass) {
            try {
                updateClass = false;
                Business bus = new Business();
                boolean am = true;
                boolean mon = true;
                String classID = "cl" + classIDtxt.getText();
                if ("3,5,7".equals((String) dayOfweekTypeComboBox_ClassEdit.getSelectedItem())) {
                    mon = false;
                }
                if ("PM".equals((String) timeSlotComboBox_classEdit.getSelectedItem())) {
                    am = false;
                }
                String courseID = (String) courseIDCombobox_ClassEditor.getSelectedItem();
                bus.updateClass(new ClassObject(classID, courseID));
                MyTableClass tbcl = new MyTableClass();
                tbcl.setData(bus.getAllClass());
                classListTable.setModel(tbcl);
                String[] ss = bus.getAllSubjectByCourse(courseID);
                System.out.println(ss[1]);
                bus.removeTimetable(classID);
                bus.addTimetable(classID, am, mon, ss[0], ss[1], ss[2], ss[3]);
                resetBt_ClassEditor.doClick();
            } catch (SQLException ex) {
                Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
            }

        } else {
            Business bus = null;
            try {
                bus = new Business();
            } catch (SQLException ex) {
                Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
            }
            boolean am = true;
            boolean mon = true;
            String classID = "cl" + classIDtxt.getText();
            if ("3,5,7".equals((String) dayOfweekTypeComboBox_ClassEdit.getSelectedItem())) {
                mon = false;
            }
            if ("PM".equals((String) timeSlotComboBox_classEdit.getSelectedItem())) {
                am = false;
            }
            String courseID = (String) courseIDCombobox_ClassEditor.getSelectedItem();
            try {
                if (bus.addClass(new ClassObject(classID, courseID))) {
                    MyTableClass tbcl = new MyTableClass();
                    tbcl.setData(bus.getAllClass());
                    classListTable.setModel(tbcl);
                    String[] ss = bus.getAllSubjectByCourse(courseID);
                    System.out.println(ss[1]);
                    bus.addTimetable(classID, am, mon, ss[0], ss[1], ss[2], ss[3]);
                    resetBt_ClassEditor.doClick();
                }


            } catch (SQLException ex) {
                JOptionPane.showMessageDialog(this, "ClassID error !", "Error", 0);
            }
        }
    }//GEN-LAST:event_enterBt_ClassEditorActionPerformed

