    private void classBtActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_classBtActionPerformed
        try {
            progress(200);
            delBt_class.setEnabled(false);

            timeTable.setVisible(false);
            classStatusTxt.setText("Please select class !");
            Business bus = new Business();
            List<String> lsCourse = new ArrayList<>();
            try {
                lsCourse = bus.getAllCourseName();
            } catch (SQLException ex) {
                Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
            }
            DefaultComboBoxModel cb1 = new DefaultComboBoxModel();
            for (String st : lsCourse) {
                cb1.addElement(st);

            }
            courseIDCombobox_ClassEditor.setModel(cb1);
            DefaultComboBoxModel cb2 = new DefaultComboBoxModel();
            cb2.addElement("2,4,6");
            cb2.addElement("3,5,7");

            dayOfweekTypeComboBox_ClassEdit.setModel(cb2);
            DefaultComboBoxModel cb3 = new DefaultComboBoxModel();
            cb3.addElement("AM");
            cb3.addElement("PM");
            timeSlotComboBox_classEdit.setModel(cb3);
            MyTableClass tbcl = new MyTableClass();

            try {

                tbcl.setData(bus.getAllClass());
                classListTable.setModel(tbcl);

            } catch (SQLException ex) {
                Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
            }

            if (classstt == 0) {
                classstt = 1;
                ClassFrame.setSize(800, 600);
                MainFrame.add(ClassFrame);
                try {
                    ClassFrame.setMaximum(true);
                } catch (PropertyVetoException ex) {
                    Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
                }

            } else {
                ClassFrame.setVisible(true);
                ClassFrame.toFront();
                MainFrame.setSelectedFrame(ClassFrame);
            }
        } catch (SQLException ex) {
            Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
        }

    }//GEN-LAST:event_classBtActionPerformed

