    private void addCourse_BtActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addCourse_BtActionPerformed
        try {
            Business bus = new Business();
            Courses co = new Courses();
            boolean ck = false;
            if ("".equals(courseID_txt.getText()) || "".equals(courseName_txt.getText()) || "".equals(courseInfo_txt.getText()) || courseSub4_cb.isVisible() == false) {
                ck = true;

            } else {

                co.setId("co" + courseID_txt.getText());
                co.setName(courseName_txt.getText());
                co.setInfo(courseInfo_txt.getText());
                co.setSb1((String) courseSub1_cb.getSelectedItem());
                co.setSb2((String) courseSub2_cb.getSelectedItem());
                co.setSb3((String) courseSub3_cb.getSelectedItem());
                co.setSb4((String) courseSub4_cb.getSelectedItem());

            }
            if (updateCourse) {
                if (ck) {

                    JOptionPane.showMessageDialog(this, "All required", "Error", 2);

                } else {
                    try {
                        bus.editCourse(co);
                    } catch (SQLException ex) {
                        JOptionPane.showMessageDialog(this, "NOT same ID", "Error", 2);
                    }

                }



            } else {

                if (ck) {

                    JOptionPane.showMessageDialog(this, "All required", "Error", 2);

                } else {
                    try {
                        bus.addCourse(co);
                    } catch (SQLException ex) {
                        JOptionPane.showMessageDialog(this, "NOT same ID", "Error", 2);
                    }

                }


            }
            resetCourse_btActionPerformed(evt);
        } catch (SQLException ex) {

            Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
        }





    }//GEN-LAST:event_addCourse_BtActionPerformed

