    private void filterSum_btActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_filterSum_btActionPerformed
        try {
            Business bus = new Business();
            List<sumAllStudent> ls = new ArrayList<>();
            boolean ckClass = true;
            if ("---".equals((String) sumClass_Cb.getSelectedItem()) || sumClass_Cb.getSelectedIndex() == -1) {
                ckClass = false;
            }
            try {
                for (sumAllStudent su : bus.getAllSum()) {
                    if (ckClass) {
                        if (su.getClassID().equals((String) sumClass_Cb.getSelectedItem())) {
                            if (sumAttend_Check.isSelected() || sumPayFines_Check.isSelected()) {
                                if (sumAttend_Check.isSelected() && sumPayFines_Check.isSelected()) {
                                    if (su.isAttendFinalExams() && su.isFines()) {

                                        ls.add(su);
                                    }

                                } else {
                                    if (sumAttend_Check.isSelected()) {

                                        if (su.isAttendFinalExams()) {

                                            ls.add(su);
                                        }
                                    } else {
                                        if (su.isFines()) {

                                            ls.add(su);
                                        }

                                    }
                                }

                            } else {

                                ls.add(su);
                            }
                        }

                    } else {
                        if (sumAttend_Check.isSelected() || sumPayFines_Check.isSelected()) {
                            if (sumAttend_Check.isSelected() && sumPayFines_Check.isSelected()) {
                                if (su.isAttendFinalExams() && su.isFines()) {

                                    ls.add(su);
                                }

                            } else {
                                if (sumAttend_Check.isSelected()) {

                                    if (su.isAttendFinalExams()) {

                                        ls.add(su);
                                    }
                                } else {
                                    if (su.isFines()) {

                                        ls.add(su);
                                    }

                                }
                            }

                        } else {

                            ls.add(su);
                        }


                    }
                }
            } catch (SQLException ex) {
                Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
            }
            MyTableSumAll mtsa = new MyTableSumAll();
            mtsa.setData(ls);
            sumView_Table.setModel(mtsa);
            if (sumClass_Cb.getSelectedIndex() == -1) {
                resetSum_Bt.doClick();
            }
        } catch (SQLException ex) {
            Logger.getLogger(Main.class.getName()).log(Level.SEVERE, null, ex);
        }


    }//GEN-LAST:event_filterSum_btActionPerformed

