    private void conOk_BtActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_conOk_BtActionPerformed
        try {
            try (ObjectOutputStream out = new ObjectOutputStream(new FileOutputStream("config.dat"))) {
                Config con = new Config();
                con.setAddress(server.getText());
                con.setPort(port.getText());
                con.setData(dataSQL.getText());
                con.setUser(usernameSQL.getText());
                con.setPass(passwordSQL.getText());
                out.writeObject(con);
            }
            Business business = new Business();
            JOptionPane.showMessageDialog(this, "Success ! Connected to database  !");
            configFrame.setVisible(false);
            this.setVisible(false);
            main(new String[1]);
        } catch (IOException | SQLException ex) {
            JOptionPane.showMessageDialog(this, "Don't connect to database ! \n Please try connection config again!", "Erorr!", 2);
        }
    }//GEN-LAST:event_conOk_BtActionPerformed

