    private void passOld_PasstxtKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_passOld_PasstxtKeyReleased
        if (passnew.equals(String.valueOf(passOld_Passtxt.getPassword()))) {
            passOldstt_lable.setText("OK");
            new1_Passtxt.setEnabled(true);
            passOldstt_lable.setForeground(Color.GREEN);
        } else {
            new1_Passtxt.setEnabled(false);
        }
    }//GEN-LAST:event_passOld_PasstxtKeyReleased

    private void new1_PasstxtKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_new1_PasstxtKeyReleased
        if (!passnew.equals(String.valueOf(new1_Passtxt.getPassword())) && !String.valueOf(new1_Passtxt.getPassword()).isEmpty()) {
            newpassStt_lable.setText("OK");
            newpassStt_lable.setForeground(Color.GREEN);
            retypenewpass_Passtxt.setEnabled(true);
        } else {
            retypenewpass_Passtxt.setEnabled(false);
            newpassStt_lable.setText("Error");
            newpassStt_lable.setForeground(Color.RED);
        }
    }//GEN-LAST:event_new1_PasstxtKeyReleased

    private void retypenewpass_PasstxtKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_retypenewpass_PasstxtKeyReleased
        if (String.valueOf(retypenewpass_Passtxt.getPassword()).equals(String.valueOf(new1_Passtxt.getPassword()))) {
            retypenewpassStt_lable.setText("OK");
            retypenewpassStt_lable.setForeground(Color.GREEN);
            okPasschange_Bt.setEnabled(true);
        } else {
            okPasschange_Bt.setEnabled(false);
            retypenewpassStt_lable.setText("Error");
            retypenewpassStt_lable.setForeground(Color.RED);
        }
    }//GEN-LAST:event_retypenewpass_PasstxtKeyReleased

